import React, { useState, useRef, useEffect } from "react";
import { useInterval } from "./useInterval";
import {
  CANVAS_SIZE,
  SNAKE_START,
  COIN_START,
  SCALE,
  SPEED,
  DIRECTIONS,
} from "./constants";

const App = () => {
  const canvasRef = useRef();
  const [snake, setSnake] = useState(SNAKE_START);
  const [coin, setCoin] = useState(COIN_START);
  const [dir, setDir] = useState([0, -1]);
  const [speed, setSpeed] = useState(500);
  const [gameOver, setGameOver] = useState(false);
  const [points, setPoints] = useState(0);

  const startGame = () => {

  };

  const endGame = () => {};

  const moveSnake = ({ keyCode }) => {
      if(keyCode >= 37 && keyCode <= 40) setDir(DIRECTIONS[keyCode]);
  }
     

  const createCoin = () => {};

  const checkCollision = () => {};

  const checkCoinCollision = () => {};

  const gameLoop = () => {
      const snakeCopy = JSON.parse(JSON.stringify(snake));
      const newSnakeHead = [snakeCopy[0][0] + dir[0], snakeCopy[0][1] + dir[1]];
      snakeCopy.unshift(newSnakeHead);
      snakeCopy.pop();
      setSnake(snakeCopy);
  };

  const countPoints = () => {};

  useEffect(() => {
    const context = canvasRef.current.getContext("2d");
    context.setTransform(SCALE, 0, 0, SCALE, 0, 0);
    context.clearRect(0, 0, CANVAS_SIZE[0], CANVAS_SIZE[1]);
    context.fillStyle = "#27f208";
    snake.forEach(([x, y]) => context.fillRect(x, y, 0.5, 1));
    context.fillStyle = "#f2c508";
    context.fillRect(coin[0], coin[1], 0.5, 1);
  }, [snake, coin, gameOver]);

  useInterval(() => gameLoop(), speed)

  return (
    <div role="button" tabIndex="0" onKeyDown={(e) => moveSnake(e)}>
      <canvas
        style={{ border: "1px solid black", backgroundColor: "#D3D3D3" }}
        ref={canvasRef}
        width={`${CANVAS_SIZE[0]}px`}
        height={`${CANVAS_SIZE[1]}px`}
      />
      {gameOver && <div>Game Over!</div>}
      <button onClick={startGame}>Start Game</button>
    </div>
  );
};

export default App;
