import React, { useState, useRef, useEffect } from "react";
import { useInterval } from "./useInterval";
import {
  CANVAS_SIZE,
  SNAKE_START,
  COIN_START,
  SCALE,
  SPEED,
  DIRECTIONS,
} from "./constants";

const App = () => {
  const canvasRef = useRef();
  const [snake, setSnake] = useState(SNAKE_START);
  const [coin, setCoin] = useState(COIN_START);
  const [dir, setDir] = useState([0, -1]);
  const [speed, setSpeed] = useState();
  const [gameOver, setGameOver] = useState(false);
  const [points, setPoints] = useState(0);

  const startGame = () => {
    setSnake(SNAKE_START);
    setCoin(COIN_START);
    setDir([0, -1]);
    setSpeed(SPEED);
    setGameOver(false);
    setPoints(0);
  };

  const endGame = () => {
    setSpeed(null);
    setGameOver(true);
  };

  const moveSnake = ({ keyCode }) => {
    if (keyCode >= 37 && keyCode <= 40) setDir(DIRECTIONS[keyCode]);
  };

  const createCoin = () => {
    coin.map((_, i) => Math.floor((Math.random() * CANVAS_SIZE[i]) / SCALE));
  };

  const checkCollision = (piece, snk = snake) => {
    if (
      piece[0] * SCALE >= CANVAS_SIZE[0] ||
      piece[0] < 0 ||
      piece[1] * SCALE >= CANVAS_SIZE[1] ||
      piece[1] < 0
    ) {
      return true;
    }
    for (const segment of snk) {
      if (piece[0] === segment[0] && piece[1] === segment[1]) return true;
    }
    return false;
  };

  const checkCoinCollision = (newSnake) => {
    if (newSnake[0][0] === coin[0] && newSnake[0][1] === coin[1]) {
      let newCoin = createCoin();
      while (checkCollision(newCoin, newSnake)) {
        newCoin = createCoin();
      }
      setCoin(newCoin);
      setPoints(points + 1);
      return true;
    }
    return false;
  };

  const gameLoop = () => {
    const snakeCopy = JSON.parse(JSON.stringify(snake));
    const newSnakeHead = [snakeCopy[0][0] + dir[0], snakeCopy[0][1] + dir[1]];
    snakeCopy.unshift(newSnakeHead);
    if (checkCollision(newSnakeHead)) endGame();
    if (!checkCoinCollision(snakeCopy)) snakeCopy.pop();
    setSnake(snakeCopy);
  };

  useEffect(() => {
    const context = canvasRef.current.getContext("2d");
    context.setTransform(SCALE, 0, 0, SCALE, 0, 0);
    context.clearRect(0, 0, CANVAS_SIZE[0], CANVAS_SIZE[1]);
    context.fillStyle = "#27f208";
    snake.forEach(([x, y]) => context.fillRect(x, y, 1, 1));
    context.fillStyle = "#f2c508";
    context.fillRect(coin[0], coin[1], 1, 1);
  }, [snake, coin, gameOver]);

  useInterval(() => gameLoop(), speed);

  return (
    <div role="button" tabIndex="0" onKeyDown={(e) => moveSnake(e)}>
      <canvas
        style={{ border: "1px solid black", backgroundColor: "#D3D3D3" }}
        ref={canvasRef}
        width={`${CANVAS_SIZE[0]}px`}
        height={`${CANVAS_SIZE[1]}px`}
      />
      {gameOver && <div>Game Over!</div>}
      <div>Score: {points}</div>
      <button onClick={startGame}>Start Game</button>
    </div>
  );
};

export default App;
